    getPokemonList() {
        (async () => {
            const api = new PokemonClient();
            var amountOfPokemons = 898; // amount of pokemon without counting variations
            var amountWanted = 20;
            var griddedPokemons = [];
            await api
                .listPokemons(0, amountOfPokemons)
                .then((data) => {
                var indexArray = new Array();
